mapper.delete:
	UPDATE article
	SET STATUS = 'S'
WHERE articleId = #{value}

UPDATE article
	SET STATUS = 'S'
WHERE parant_id = #{value}

mapper.select:
SELECT A.articleId, A.regDate, A.updateDate, A.title, A.BODY, A.hit, A.STATUS, A.children, A.parant_id AS patantId, A.board_id AS boardId, A.writer_id AS writerId, B.nickname
FROM `article` A INNER JOIN `member` B ON A.writer_id = B.memberId
WHERE articleId = #{articleId}

mapper.update:
UPDATE article
	SET hit = hit + 1
WHERE articleId = #{articleId}

UPDATE article
	SET children = children + 1
WHERE articleId = #{articleId}

UPDATE article

SET	`updateDate` = NOW(),
	`title` = #{title},
	`body` = #{body}

WHERE articleId = #{articleId};

mapper.select:
SELECT A.articleId, A.regDate, A.updateDate, A.title, A.BODY, A.hit, A.STATUS, A.children, A.parant_id AS patantId, A.board_id AS boardId, A.writer_id AS writerId, B.nickname
FROM `article` A INNER JOIN `member` B ON A.writer_id = B.memberId
WHERE board_id = #{boardId} AND writer_id = #{memberId}
