mapper.select:
SELECT * FROM `event` e, `member` m WHERE e.provider_id = m.memberId AND e.`status` != 'S'
AND e.`status` = #{option}
ORDER BY e.regDate DESC LIMIT #{startLimit}, #{endLimit}

SELECT @rowNum := @rowNum + 1 AS 'num', DATE_FORMAT(a.regDate, '%Y-%m-%d') AS 'regDate', DATE_FORMAT(a.dueDate, '%Y-%m-%d') AS 'dueDate', a.couponNumber AS 'couponNumber', a.couponName AS 'couponName', a.status AS 'status', a.couponPrice AS 'couponPrice', a.discountRate AS 'discountRate', a.eventId AS 'eventId'
FROM (SELECT @rowNum := #{currentPage}) tmp, (SELECT b.eventId, b.regDate, b.dueDate, a.couponNumber, a.couponName, b.status, a.couponPrice, a.discountRate
FROM `coupon` a, `event` b WHERE a.event_id = b.eventId
AND b.status = #{option}
) a LIMIT #{currentPage}, #{listPerPage}
