mapper.select:
SELECT COUNT(*) AS result
FROM `member`
WHERE email = #{email};

SELECT COUNT(*) AS result
FROM `member`
WHERE nickname = #{nickname};

SELECT COUNT(*) AS result
FROM `member`
WHERE phone = #{phone};

SELECT *
FROM member

WHERE 
    status = #{option}

ORDER BY memberId DESC
    LIMIT #{startLimit}, #{endLimit}

SELECT COUNT(*) AS result
FROM member

    WHERE status = #{option}

mapper.update:
        UPDATE member
		SET STATUS = #{status}
		WHERE memberId = #{memberId}
